generated.css:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

ul, ol {
    list-style: none;
}

a {
    color: inherit;
    text-decoration: none;
}

img {
    max-width: 100%;
}

body {
    background-color: #eee;
}

.globalHeader {
    margin: 20px;
}

@media (min-width: 500px) {
    .globalHeader {
        margin: 60px 0px;
    }
}

.searchForm {
    display: flex;
    justify-content: space-between;
    max-width: 400px;
    height: 40px;
}

@media (min-width: 500px) {
    .searchForm {
        max-width: 400px;
        margin-left: auto;
        margin-right: auto;
    }
}

.globalMain {
    max-width: 900px;
    margin-left: auto;
    margin-right: auto;
}

.searchForm input {
    border: 1px solid #ddd;
    border-radius: 4px;
    width: 100%;
    padding-left: 10px;
    margin-right: 10px;
}

.searchForm button {
    width: 85px;
    background-color: #227CBD;
    border: 1px solid #227CBD;
    border-radius: 4px;
    color: white;
    font-size: 16px;
}

.siteList {
    display: flex;
    flex-wrap: wrap;
    margin: 0 18px;
    justify-content: space-between;
    /* border: 1px solid red; */
}

@media (min-width: 500px) {
    .siteList {
        margin-left: 0;
        margin-right: -25px;
        justify-content: flex-start;
    }
}

.siteList > li {
    margin-bottom: 16px;
    /* border: 1px solid blue; */
}

@media (min-width: 500px) {
    .siteList > li {
        margin-right: 25px;
    }
}

.site {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;
    background-color: white;
    border: 1px solid #ddd;
    width: 160px;
    height: 130px;
    position: relative;
    cursor: pointer;
    border-radius: 4px;
    padding: 20px 0;
    position: relative;
}

.site .iconfont{
    position: absolute;
    right: 10px;
    top: 5px;
    display: none;
}

.site:hover .iconfont{
    display: block;
}

.site .icon {
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 64px;
    text-transform: uppercase;
}



.site .text {
    font-size: 14px;
    margin-top: 4px;
    text-align: center;
}

.addButton {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 160px;
    height: 130px;
    padding: 20px 0;
    background-color: white;
    border: 1px solid #ddd;
    border-radius: 4px;
}

.addButton .iconfont {
    display: flex;
    justify-content: center;
    font-size: 64px;
}

.addButton text {
    font-size: 14px;
    margin-top: 10px;
}
